import 'package:flutter/material.dart';import 'package:sunnah_app/screen/main_screen.dart';import 'package:sunnah_app/screen/profile_screen.dart';import 'package:sunnah_app/screen/search_screen.dart';class HomePage extends StatefulWidget {  const HomePage({Key? key}) : super(key: key);  @override  State<HomePage> createState() => _HomePageState();}class _HomePageState extends State<HomePage> {  @override  int pageIndex = 0;  final pages = [    const MainScreen(),    const SearchScreen(),    const ProfileScreen(),  ];  @override  Widget build(BuildContext context) {    return Scaffold(      body: pages[pageIndex],      bottomNavigationBar: Container(        height: 80,        decoration: const BoxDecoration(          color: Colors.white,        ),        child: Row(          mainAxisAlignment: MainAxisAlignment.spaceAround,          children: [            IconButton(              enableFeedback: false,              onPressed: () {                setState(() {                  pageIndex = 0;                });              },              icon: pageIndex == 0                  ? const Icon(                Icons.home_filled,                color: Colors.green,                size: 35,              )                  : const Icon(                Icons.home_outlined,                color: Colors.black12,                size: 35,              ),            ),            IconButton(              enableFeedback: false,              onPressed: () {                setState(() {                  pageIndex = 1;                });              },              icon: pageIndex == 1                  ? const Icon(                Icons.search_rounded,                color: Colors.green,                size: 35,              )                  : const Icon(                Icons.search_outlined,                color: Colors.black12,                size: 35,              ),            ),            IconButton(              enableFeedback: false,              onPressed: () {                setState(() {                  pageIndex = 2;                });              },              icon: pageIndex == 2                  ? const Icon(                Icons.account_circle_rounded,                color: Colors.green,                size: 35,              )                  : const Icon(                Icons.account_circle_outlined,                color: Colors.black12,                size: 35,              ),            ),          ],        ),      ),    );  }}